//import {useContext} from "react";
//import BookContext from "../../Context/BookContext";

import { useState } from "react";

export const Bookcreat = () => {
//const { formValues, onChange, storeBook} = useContext(BookContext);
const [formValues, setFormValues] = useState({
  title: "",
  author:"",
  publication_year:"",
  ISBN:"",
  no_in_stock:"",
})
const onChange = (e) => {
  const {name, value} = e.target;
  
}
  return (
    <div className="mt-12">
      <form className=" max-w-md mx-auto p-4 bg-white shadow-md rounded-sm">
        <div className="space-y-6">
          <div className="mb-4">
            <label htmlFor="title" className="blockmb-2 text-sm font-medium">
              Title
            </label>
            <input name="title" value={formValues["title"]} onChange={onChange} className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"/>
          </div>
          <div className="mb-4">
            <label htmlFor="author" className="blockmb-2 text-sm font-medium">
              Author
            </label>
            <input className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"/>
          </div>
          <div className="mb-4">
            <label htmlFor="publication_year" className="blockmb-2 text-sm font-medium">
            Publication Year
            </label>
            <input className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"/>
          </div>
          <div className="mb-4">
            <label htmlFor="ISBN" className="blockmb-2 text-sm font-medium">
            ISBN
            </label>
            <input className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"/>
          </div>
          <div className="mb-4">
            <label htmlFor="no_in_stock" className="blockmb-2 text-sm font-medium">
            No In Stock
            </label>
            <input className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"/>
          </div>
          <div className="my-4">
            <button className="px-4 py-2 bg-indigo-500 hover:bg-indigo-700 text-white rounded-md">Store</button>
          </div>
        </div>
      </form>
    </div>
  );
};
